import React from 'react';
import PlaylistCard from './PlaylistCard';
import { withRouter } from 'react-router-dom';

class WebPlayer extends React.Component{
  state = {
    featured: [],
    top: [],
    pod: []
  }

  componentDidMount = () => {
    Object.keys(this.props.user).length > 0
    ? fetch("http://api.napster.com/v2.2/playlists/featured?apikey=MTU1YjllNjUtOTIwNi00MGJlLWJlOWMtZGYxMjJhZDI0NTk5&limit=5")
    .then(resp => resp.json())
    .then(featuredPlaylist => {
      this.setState({
        featured: featuredPlaylist.playlists
      })
    })
    : this.props.history.push("/login")

    Object.keys(this.props.user).length > 0
    ? fetch("http://api.napster.com/v2.2/playlists/top?apikey=MTU1YjllNjUtOTIwNi00MGJlLWJlOWMtZGYxMjJhZDI0NTk5&limit=5")
    .then(resp => resp.json())
    .then(topPlaylist => {
      this.setState({
        top: topPlaylist.playlists
      })
    })
    : this.props.history.push("/login")
  }

  renderfeaturedToPage = () => {
    const newArr = this.state.featured.map( playlist => {
      return <PlaylistCard key={playlist.id} playlist={playlist} selectedPlaylistCard={this.props.selectedPlaylistCard}/>
    })
      return newArr
  }

  renderTopToPage = () => {
    const newArr = this.state.top.map( playlist => {
      return <PlaylistCard key={playlist.id} playlist={playlist} selectedPlaylistCard={this.props.selectedPlaylistCard}/>
    })
      return newArr
  }

  render(){
    console.log('web player state', this.state);
    return(
      <div className="featured">
        <h3>Web Player</h3>
        <div>
        <h1>Featured</h1>
        {this.renderfeaturedToPage()}
        </div>
        <div>
        <h1>Top</h1>
        {this.renderTopToPage()}
        </div>

      </div>
    )
  }
}

export default withRouter(WebPlayer);
