import React from 'react';
import { ActionCable } from 'react-actioncable-provider';
import { API_ROOT } from '../constants';
import NewWebplayerForm from './NewWebplayerForm';
import UserSongsArea from './UserSongsArea';
import Cable from './Cable';
import { Button, Header, Icon, Image, Menu, Segment, Sidebar } from 'semantic-ui-react';


  // const webPlayer =

class WebplayerList extends React.Component {
  state = {
    webplayers: [],
    activeWebplayer: null,
    visible:false
  };

  //semantic pushable side navbar
  handleHideClick = () => this.setState({ visible: false })
  handleShowClick = () => this.setState({ visible: true })
  handleSidebarHide = () => this.setState({ visible: false })

  componentDidMount = () => {
    let token = localStorage.token
    fetch(`${API_ROOT}/webplayers`, {
      method: 'GET',
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${token}`
      },
    })
    .then(resp => resp.json())
    .then( webplayers => {
      this.setState({ webplayers: webplayers })
    });
  };

  componentDidUpdate = (prevState) => {
    if (this.state.webplayers !== prevState.webplayers) {
      let token = localStorage.token
      fetch(`${API_ROOT}/webplayers`, {
        method: 'GET',
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
          Authorization: `Bearer ${token}`
        },
      })
      .then(resp => resp.json())
      .then( webplayers => this.setState({ webplayers: webplayers }));
    }
  }

  handleClick = id => {
    this.setState({ activeWebplayer: id });
  };

  handleRecievedWebplayer = response => {
    const { webPlayer } = response;
    this.setState({
        webplayers: [...this.state.webplayers, webPlayer ]
    });
  };

//sets up the chat to see who is in the chat
  handleRecievedUserSong = response => {
    const { userSong } = response;
    const webplayers = [...this.state.webplayers];
    const webplayer = webplayers.find(
      webplayer => webplayer.id === userSong.webplayer_id
    );
    webplayer.userSongs = [...webplayer.userSongs, userSong];
    this.setState({ webplayer })
  };


  render = () => {
    console.log('WEBPLAYERLIST', this.state.webplayers);
    const { webplayers, activeWebplayer, visible } = this.state;
    return (
      <div className="webplayersList">
        <ActionCable
          channel={{ channel: 'webplayers_channel' }}
          onReceived={this.handleRecievedWebplayer}
        />
        {this.state.webplayers.length ? (
          <Cable
            webplayers={webplayers}
            handleRecievedUserSong={this.handleRecievedUserSong}
          />
        ) : null}
        <h2>Webplayers</h2>
        <ul>{mapWebplayers(webplayers, this.handleClick, this.props.user)}</ul>
        <NewWebplayerForm/>
        {activeWebplayer ? (
          <UserSongsArea
          webplayer={findActiveWebplayer(
            webplayers,
            activeWebplayer
          )}
          />
        ):null}
        <div class="demo">
        <svg class="sidebar" viewBox="0 0 300 500">
        <path class="s-path" fill="#fff" d="M0,0 50,0 a0,250 0 1,1 0,500 L0,500" />
        </svg>
        <div class="static">
        <div class="static__text">Pull white sidebar to the right</div>
        </div>
        <div class="sidebar-content">
        <div class="contact">
        <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/142996/elastic-man.png" alt="" class="contact__photo" />
        <span class="contact__name">Ethan Hawke</span>
        <span class="contact__status online"></span>
        </div>
        <div class="contact">
        <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/142996/elastic-girl.png" alt="" class="contact__photo" />
        <span class="contact__name">Natalie Portman</span>
        <span class="contact__status online"></span>
        </div>
        <div class="contact">
        <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/142996/elastic-man.png" alt="" class="contact__photo" />
        <span class="contact__name">Kevin Spacey</span>
        <span class="contact__status online"></span>
        </div>
        <div class="contact">
        <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/142996/elastic-girl.png" alt="" class="contact__photo" />
        <span class="contact__name">Rosamund Pike</span>
        <span class="contact__status online"></span>
        </div>
        <div class="contact">
        <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/142996/elastic-man.png" alt="" class="contact__photo" />
        <span class="contact__name">Robert Redford</span>
        <span class="contact__status online"></span>
        </div>
        <div class="contact">
        <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/142996/elastic-girl.png" alt="" class="contact__photo" />
        <span class="contact__name">Scarlett Johansson</span>
        <span class="contact__status online"></span>
        </div>
