import React from 'react'
import { withRouter } from 'react-router-dom';
import Wavelength from './Wavelength'
//css
import '../style/sass/components/songDetail.scss'

class SongDetail extends React.Component{
  state = {
    playing: false,
    playingURL: "",
    audio: null,
    playing: false,
    title: ''
  }

  playAudio(previewURL){
    let audio = new Audio(previewURL)
    audio.play()
    this.setState({
      playing: true,
      playingURL: previewURL,
      audio: audio
    })
  }

  pauseAudio(previewURL){
    console.log("pause audio")
    let audio = new Audio(previewURL)
    if (this.state.playingURL === previewURL) {
      this.state.audio.pause()
      this.setState({
        playing: false
      }, ()=>console.log("set playing to false",this.state.playing))
    }
  }
  //
  // handleChange = e => {
  //   this.setState({ title: e.target.value })
  // };

  render(){
    console.log('song detail', this.props);
    return(
      <div>
        <button  onClick={ () => this.playAudio(this.props.currentSong.previewURL) }></button>
        <button  onClick={ () => this.pauseAudio(this.props.currentSong.previewURL)}></button>
      </div>
    )
  }
}

export default withRouter(SongDetail);
