import React from 'react'
import { withRouter } from 'react-router-dom';

class WebPlayer extends React.Component{
  state = {
    featured: [],
    top: [],
    pod: []
  }

  componentDidMount = () => {
    Object.keys(this.props.user).length > 0
    ? fetch("http://api.napster.com/v2.2/playlists/featured?apikey=MTU1YjllNjUtOTIwNi00MGJlLWJlOWMtZGYxMjJhZDI0NTk5")
    .then(resp => resp.json())
    .then(featuredPlaylist => {
      this.setState({
        featured: featuredPlaylist.playlists
      })
    })
    : this.props.history.push("/login")
  }

  renderfeaturedToPage = () => {
    const newArr = this.state.featuredPlaylist.map( playlist => {
      return <PlaylistCard key={playlist.id} playlist={playlist} selectedPlaylistCard={this.props.selectedPlaylistCard}/>
    })
      return newArr
  }
  renderPlaylistCard = () => {
    const newArr = this.state.playlistsArr.map( playlist => {
      return <PlaylistCard key={playlist.id} playlist={playlist} selectedPlaylistCard={this.props.selectedPlaylistCard}/>
    })
      return newArr
  }

  render(){
    console.log('web player state', this.state);
    return(
      <div>
        <h3>Web Player</h3>
      </div>
    )
  }
}

export default withRouter(WebPlayer);
