import React from 'react';
import PlaylistCard from './PlaylistCard';
import { withRouter } from 'react-router-dom';

class WebPlayer extends React.Component{
  state = {
    top: [],
    allPlaylists: [],
    favPlayistId: [],
    userPlaylists: []
  }

  componentDidMount = () => {
    let token = localStorage.token
    //top playlist
    Object.keys(this.props.user).length > 0
    ? fetch("http://api.napster.com/v2.2/playlists/top?apikey=MTU1YjllNjUtOTIwNi00MGJlLWJlOWMtZGYxMjJhZDI0NTk5&limit=6")
    .then(resp => resp.json())
    .then(topPlaylist => {
      this.setState({
        top: topPlaylist.playlists
      })
    })
    : this.props.history.push("/login")
    // top albums
    Object.keys(this.props.user).length > 0
    ? fetch("http://api.napster.com/v2.2/playlists?apikey=MTU1YjllNjUtOTIwNi00MGJlLWJlOWMtZGYxMjJhZDI0NTk5&limit=-2")
    .then(resp => resp.json())
    .then(allPlaylists => {
      this.setState({
        allPlaylists: allPlaylists.playlists
      })
    })
    : this.props.history.push("/login")
    //fetch to get user playlists
    Object.keys(this.props.user).length > 0
    ? fetch("http://localhost:3000/api/v1/user_playlists", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${token}`
      },
    })
    .then(resp => resp.json())
    .then(favPlayist => {
      this.setState({
        favPlayistId: favPlayist
      })
      this.state.favPlayistId.forEach(function(favPlayistId){
        fetch(`http://api.napster.com/v2.2/playlists/${favPlayistId.napster_playlist_id}?apikey=MTU1YjllNjUtOTIwNi00MGJlLWJlOWMtZGYxMjJhZDI0NTk5`)
      })


      // favPlayistId.napster_playlist_id
      fetch(`http://api.napster.com/v2.2/playlists/${this.state.favPlayistId[0].napster_playlist_id}?apikey=MTU1YjllNjUtOTIwNi00MGJlLWJlOWMtZGYxMjJhZDI0NTk5`)
      .then(resp => resp.json())
      .then(playlistObj => {
        this.setState({
          userPlaylists: playlistObj.playlists
        })
      })
    })
    : this.props.history.push("/login")
  }

  getPlaylistsIds = () => {
    const ids = this.state.favPlayistId.map(playlist => playlist.napster_playlist_id)
    return ids
  }
  renderTopToPage = () => {
    const newArr = this.state.top.map( playlist => {
      return <PlaylistCard key={playlist.id} playlist={playlist} selectedPlaylistCard={this.props.selectedPlaylistCard}/>
    })
      return newArr
  }
  renderAllPlaylistsToPage = () => {
    const newArr = this.state.allPlaylists.map( playlist => {
      return <PlaylistCard key={playlist.id} playlist={playlist} selectedPlaylistCard={this.props.selectedPlaylistCard} addPlayistToUser={this.addPlayistToUser}/>
    })
      return newArr
  }

  renderUserPlaylistToPage = () => {
    const newArr = this.state.userPlaylists.map( playlist => {
      return <PlaylistCard key={playlist.id} playlist={playlist} selectedPlaylistCard={this.props.selectedPlaylistCard} addPlayistToUser={this.addPlayistToUser}/>
    })
      return newArr
  }

  render(){
    debugger

    console.log('web player state', this.state);
    console.log('web player props', this.props);
    return(
      <div className="featured">
        <h3>Web Player</h3>
        <div>
        <h1>Recently Played</h1>
        {this.renderUserPlaylistToPage()}
        </div>
        <div>
        <h1>Top</h1>
        {this.renderTopToPage()}
        </div>
        <div>
        <h1>A Little Bit Of Everything</h1>
        {this.renderAllPlaylistsToPage()}
        </div>

      </div>
    )
  }
}

// USE TO GET THE TRACKS FOR A USER PLAYLIST
