//   fetch(playlistObj.playlists[0].links.tracks.href + "?apikey=MTU1YjllNjUtOTIwNi00MGJlLWJlOWMtZGYxMjJhZDI0NTk5&limit=10")
//   .then(resp => resp.json())
//   .then(tracksArr => {
//     this.setState({
//       userPlaylists: tracksArr.tracks
//     })
//   })
// })

export default withRouter(WebPlayer);
