import React, { Component } from 'react';
import logo from './logo.svg';
//need to have both the route and switch
//Switch stops at the first match
import { Route, Switch, withRouter } from "react-router-dom";

import Home from './components/Home';
import PlaylistContainer from './components/PlaylistContainer';
import ArtistContainer from './components/ArtistContainer';
import SongContainer from './components/SongContainer';
import ProfileContainer from './components/ProfileContainer';
import SignUp from './components/SignUp';
import Login from './components/Login';
import Error from './components/Error';
import './App.css';

// <Route exact path="/playlists/:name" render={()=> <PlaylistContainer/>} />
class App extends Component {
  state = {
    user: {}
  }

  //allows the user to stay logged in
  // if user? take the token from localStorage and send in to the backend

  componentDidMount = () => {
    let token = localStorage.token
    localStorage.token ? (
      fetch("http://localhost:3000/api/v1/current_user",{
        method: "GET"  
      })
      .then(resp => resp.json())
      .then(user )
    ) : (this.props.history.push("/signup"))
  }

  handleSubmitForLogin = (user) => {
    console.log("submitted");
  };

  handleSubmitForSignUp = (user) => {
    // console.log("submitted");
    fetch("http://localhost:3000/api/v1/users", {
      method: "POST",
      headers: {
        "content-type": "application/json",
        accepts: "application/json"
      },
      body: JSON.stringify({user: user})
    })
    .then(resp => resp.json())
    .then(user => {
      this.setState({user:user}, () => {
        localStorage.setItem("token", user.user.id)
        this.props.history.push("/playlists")
      });
    });
  };

  render() {
    //console.log('app state', this.state)
    return (
      <div>
        <Switch>
          <Route exact path="/playlists" render={()=> <PlaylistContainer user={this.state.user}/>} />
          <Route exact path="/artists" render={()=> <ArtistContainer/>} />
          <Route exact path="/profiles" render={()=> <ProfileContainer/>} />
          <Route exact path="/signup" render={()=> <SignUp handleSubmitForSignUp={this.handleSubmitForSignUp}/>} />
          <Route exact path="/login" render={()=> <Login handleSubmitForLogin={this.handleSubmitForLogin}/>} />
          <Route path="/home" render={() => <Home user={this.state.user} />} />
          <Route path="/" component={Error} />
        </Switch>
      </div>
    );
  }
}

export default withRouter(App);
