import React from 'react';
import { withRouter } from 'react-router-dom';

class SongCard extends React.Component{
  state = {
    albumImage: []
  }

  componentDidMount = () => {
    const apiKey = '?apikey=MTU1YjllNjUtOTIwNi00MGJlLWJlOWMtZGYxMjJhZDI0NTk5&limit=10';
    const href = this.props.track.links.albums.href
    const url = `${href}${apiKey}`
    url
      ? fetch(url)
      .then(resp => resp.json())
      .then(albumsArr => {
        this.setState({
          albumImage: albumsArr
        })
      })
      : console.log('hi');
  }

  //<img src={this.state.albumImage.albums[0].links.images.href}/>
  renderImage = () => {
    if (this.state.albumImage) {
      return <img src={this.state.albumImage.albums[0].links.images.href}/>
    }
  }
  render(){
    console.log("album image", this.state.albumImage);
    console.log("songCard props", this.props.track.links.albums.href);
    return(
      <div>
        <h2>{this.props.track.name}</h2>
        <h3>{this.props.track.artistName}</h3>
        <h4>{this.props.track.albumName}</h4>
        {this.renderImage()}
      </div>
    )
  }
}

export default withRouter(SongCard);
