import React from 'react';
import SongCard from './SongCard';
import { Link, withRouter } from 'react-router-dom';
//withRouter gives the app the props
class SongList extends React.Component{
  state = {
    trackList: [],
    // currentAudio: null
  }

 // handleSelectedAudio =(audio)=>{
 //   this.setState({
 //     currentAudio: audio
 //   })
 // }

  componentDidMount = () => {
    const apiKey = '?apikey=MTU1YjllNjUtOTIwNi00MGJlLWJlOWMtZGYxMjJhZDI0NTk5&limit=10';
    const href = this.props.currentPlaylist.links.tracks.href
    const url = `${href}${apiKey}`
    console.log('URL THAT IM GETTING',url);
    url
      ? fetch(url)
      .then(resp => resp.json())
      .then(tracksData => {
        this.setState({
          trackList: tracksData.tracks
        })
      })
      : console.log('hi');
  }

  renderTracks = () => {
      const newArr = this.state.trackList.map(track => {
        return <SongCard key={track.id} track={track} selectedTrackCard={this.props.selectedTrackCard} currentPlaylist={this.props.currentPlaylist}/>
      })
      return newArr
  }

  savePlaylist = (event) => {
    fetch("http://localhost:3000/api/v1/user_playlists", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json"
      },
      body: JSON.stringify({
        user_id: this.props.user.id,
        playlist_id: this.props.currentPlaylist.id
      })
    })
    .then(resp => resp.json())
    .then(playlist => {this.props.addPlayistToUser(this.state)})
  }

  render(){
    console.log("SongList props", this.props);
    debugger
    return(
      <div>
      <h1>Song List</h1>
      <button onClick={this.savePlaylist}>Add Playlist To Library</button>
      {this.renderTracks()}
      </div>
    )
  }
}

export default withRouter(SongList);
