import React from 'react';
import ShowPlaylists from './ShowPlaylist';
import ShowAlbums from './ShowAlbums';


class Main extends React.Component{
  state = {
    media: [],
    selectedAlbum: null
  }
  componentDidMount = () => {
    fetch("http://api.napster.com/v2.2/playlists/featured?apikey=MTU1YjllNjUtOTIwNi00MGJlLWJlOWMtZGYxMjJhZDI0NTk5")
    .then(resp => resp.json())
    .then(playlistObj => this.setState({
      media: playlistObj.playlists
    }))
  }

  render(){
    return(
      <main>
  <div className="albums">
    {this.state.selectedAlbum
      ? <ShowPlaylists
        clearSelectedAlbum={this.clearSelectedAlbum}
        selectedAlbum={this.state.selectedAlbum}
        playlist={this.props.playlist}
        updatePlaylist={this.props.updatePlaylist}
        playlistIsPlaying={this.props.playlistIsPlaying}
        currentSongIndex={this.props.currentSongIndex} />
      : <ShowAlbums
        media={this.state.media}
        selectAlbum={this.selectAlbum}
        updatePlaylist={this.props.updatePlaylist}
        currentSongIndex={this.props.currentSongIndex}
        playlist={this.props.playlist}
        playlistIsPlaying={this.props.playlistIsPlaying} />}
  </div>
  <div>
    RMV (Version 1.1.0) Hidden
    <br />
    Select your theme:
    <select value={this.props.theme} onChange={(ev) => this.props.handleThemeChange(ev)}>
      <option value="spotify">Spotify</option>
      <option value="soundcloud">soundcloud</option>
      <option value="youtube">youtube</option>
    </select>
  </div>
</main>
    )
  }
}

export default Main;
