import React, { Component } from 'react';
import logo from './logo.svg';
//need to have both the route and switch
//Switch stops at the first match
import { Route, Switch, withRouter } from "react-router-dom";
import ReactMediaVisualizer from 'react-media-visualizer';

import Home from './components/Home';
import PlaylistContainer from './components/PlaylistContainer';
import ArtistContainer from './components/ArtistContainer';
import SongList from './components/SongList';
import ProfileContainer from './components/ProfileContainer';
import SignUp from './components/SignUp';
import Login from './components/Login';
import Error from './components/Error';
import './App.css';




// <Route exact path="/playlists/:name" render={()=> <PlaylistContainer/>} />
class App extends Component {
  state = {
    user: {},
    currentPlaylist:{},
    playlist: null,
    playlistIsPlaying: false,
    currentSongIndex: 0,
    theme: 'youtube'
  }
  //allows the user to stay logged in
  // if user? take the token from localStorage and send in to the backend
  // check to see if there is a user
  // if there is a user fetch to current_user
  //add the key Authorization that is recieving the token
  componentDidMount = () => {
    let token = localStorage.token;
    token
     ? fetch("http://localhost:3000/api/v1/current_user",{
        method: "GET",
        headers: {
          "content-type": "application/json",
          accepts: "application/json",
          Authorization: `Bearer ${token}`
        }
      })
      .then( resp => resp.json())
      .then(user => {
        this.setState({user: user }, () => {
          this.props.history.push("/playlists")
        });
      })
    : (this.props.history.push("/signup"))
  };


  handleSubmitForSignUp = (userData) => {
    fetch("http://localhost:3000/api/v1/users", {
      method: "POST",
      headers: {
        "content-type": "application/json",
        accepts: "application/json"
      },
      body: JSON.stringify({ user: userData })
    })
    .then(resp => resp.json())
    .then(userInfo => {
      this.setState({ user: userInfo.user }, () => {
        localStorage.setItem("token", userInfo.jwt )
        this.props.history.push("/playlists")
      });
    });
  };

  handleSubmitForLogin = (userData) => {
    fetch("http://localhost:3000/api/v1/login", {
        method: "POST",
        headers: {
          "content-type": "application/json",
          accepts: "application/json"
        },
        body: JSON.stringify({ user: userData })
      })
        .then(resp => resp.json())
        .then(
          userInfo => this.setState({ user: userInfo.user }),
          () => this.props.history.push("/playlists")
        );
  }

  selectedPlaylistCard = (playlistObj) => {
    this.setState({
      currentPlaylist: playlistObj
    })
  }

  render() {

    //console.log('app state', this.state)

    //browser router listens for the change in route and tells the application what to do based on the route.
    return (
      <div>
        <Switch>
          <Route path="/playlists/:name"
            render={()=> <SongList currentPlaylist={this.state.currentPlaylist} trackList={this.state.trackList}/>}/>
          <Route exact path="/playlists"
            render={()=> <PlaylistContainer user={this.state.user} selectedPlaylistCard={this.selectedPlaylistCard}/> } playlist={this.state.playlist}
            receiveStateUpdates={this.receiveStateUpdates}
            playlistIsPlaying={this.state.playlistIsPlaying}
            theme={this.state.theme}
            currentSongIndex={this.state.currentSongIndex}/>
          <Route
            exact path="/artists"
            render={ () => <ArtistContainer/> } />
          <Route
            exact path="/profiles"
            render={ () => <ProfileContainer/> } />
          <Route
            exact path="/signup"
            render={ () => <SignUp handleSubmitForSignUp={this.handleSubmitForSignUp}/> } />
          <Route
            exact path="/login"
            render={ () => <Login handleSubmitForLogin={this.handleSubmitForLogin}/> } />
          <Route
            path="/home"
            render={ () => <Home user={this.state.user} /> } />
          <Route
            path="/"
            component={Error} />
        </Switch>
      </div>
    );
  }
}


export default withRouter(App);
