import React from 'react'
import { withRouter } from 'react-router-dom';
import { Button, Form } from 'semantic-ui-react'

//scss
import '../style/sass/components/login.scss'

class Login extends React.Component{
  state = {
    email: "",
    password: ""
  }

// Logout
//Onclick = { () => {
// localStorage.removeItem("token");
// this.props.history.push("signup");
// }}

  changeHandler = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  handleSubmitForLogin = (event) => {
    event.preventDefault();
    this.props.handleSubmitForLogin(this.state);
    this.setState({
      email: "",
      password: ""
    });
  };

  render(){
    // console.log(this.props);
    return(
      <div className="login">
      <Form size={'massive'} key="{'massive'}" onSubmit={this.handleSubmitForLogin}>
        <Form.Field>
        <label>Email</label>
        <input type="text" placeholder="Email" name="email" value={this.state.email} onChange={this.changeHandler}/><br/>
        </Form.Field>
        <Form.Field>
        <label>Last Name</label>
          <input type="text" placeholder="Password" name="password" value={this.state.password} onChange={this.changeHandler}/>
        </Form.Field>
        <Button type='submit'>Log In</Button>
      </Form>
      </div>
    )
  }
}
export default withRouter(Login);
